            <td>  {{ frappe.utils.fmt_money(item.amount,currency=doc.currency) }}</td>


    </tr>
    {%endfor%}
    <tr>
        <td colspan=2>Total</td>
        <td colspan=4>  {{ frappe.utils.fmt_money(doc.grand_total,currency=doc.currency) }}</td>

    </tr>
